sysauto_script.script:
// Get today's time and date
var rightNow = new GlideDateTime();

var nextTasks = new GlideRecord('x_58872_needit_needit_task');
nextTasks.addQuery('due_date','>=', rightNow);
nextTasks.addQuery('due_date','<',gs.hoursAgo(-60));//due date within 24 hours from now
nextTasks.addQuery('state','!=',3);//a state field do not have Closed Complete

nextTasks.query();
while(nextTasks.next()){
	gs.info('Tasks to execute record = '+nextTasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',nextTasks,nextTasks.number,gs.getUserName());
}
